var name = ' Foo '
name.trim()  // -> 'Foo'
name // -> ' Foo '

// Trim returned a new value that had the whitespace
// removed, but didn't change the string saved as
// name.

var kid =
  { screaming: false
  , isCute: function () { return !this.screaming }
  }

kid.screaming = true // our state changes.
kid.isCute() // now returns false,
// because the state of kid has mutated.

